// Create filename
const filename = `${customerData.companyName.replace(/[^a-z0-9]/gi, '_')} - 30 Daga AI Roadmap.md`;

return {
  json: {
    email: 'info@lioratech.is',  // SEND TO YOU, not customer
    customerEmail: customerData.email,  // Save for reference
    subject: subject,
    body: emailBody,
    roadmapContent: roadmapContent,
    filename: filename,
    companyName: customerData.companyName
  }
};

// Create binary data for attachment
const roadmapContent = $input.item.json.roadmapContent;
const filename = $input.item.json.filename;

// Convert to binary
const binaryData = Buffer.from(roadmapContent, 'utf-8');

// Return with binary attachment
return {
  json: $input.item.json,
  binary: {
    data: {
      data: binaryData.toString('base64'),
      mimeType: 'text/markdown',
      fileName: filename
    }
  }
};

// Log successful delivery
const companyName = $input.item.json.companyName;
const email = $input.item.json.email;
const timestamp = new Date().toISOString();

console.log(`‚úÖ Roadmap delivered to ${companyName} (${email}) at ${timestamp}`);

return {
  json: {
    success: true,
    message: `Roadmap sent to ${email}`,
    timestamp: timestamp
  }
};
